package com.itmo.wineup.features.catalog

import androidx.lifecycle.ViewModel

class CatalogViewModel: ViewModel() {
    fun addWines(): ArrayList<WineModel> {
        val wines = arrayListOf<WineModel>()
        for (i in 0..9){
            wines.add(WineModel("Canti Merlot", "Франция", "красное", "сухое",
                "0.75л", 85, 4.5F, 2300, 15, "https://cdn.aldi-digital.co.uk//Toro-Loco-Organic-Red-A.jpg?o=KmIe1J1A%40XPVgDzQuVFt3%24hdkrEj&V=Zfrj&w=240&h=300&p=2&q=87", "data:image/png;base64,"))
        }
        return wines
    }
}